
public class If_Demo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=5, b=10;
		if(a>b) //&&- Logical AND || - Logical OR
		{
			System.out.println("a is greater");
		}
		else
		{
			System.out.println("b is greater");
		}
		System.out.println("end");
	}

}
